class UI {
  constructor() {
    this.post = document.querySelector("#posts");
    this.titleInput = document.querySelector("#title");
    this.bodyInput = document.querySelector("#body");
    this.idInput = document.querySelector("#id");
    this.postsSubmit = document.querySelector(".post-submit");
    this.forState = "add";
  }

  //Show posts
  showPosts(posts) {
    let output = ``;

    posts.forEach((post) => {
      output += `<div class="card mb-3">
          <div class="card-body">
            <h4 class="card-title">${post.title}</h4>
            <p class="card-text">${post.body}</p>
            <a href="#" class="edit card-link" data-id="${post.id}">
              <i class="fa fa-pencil"></i>
            </a>

            <a href="#" class="delete card-link" data-id="${post.id}">
            <i class="fa fa-remove"></i>
          </a>
          </div>
        </div>`;
    });

    this.post.innerHTML = output;
  }

  // Show alert message
  showAlert(message, className) {
    this.clearAlert();

    // Create div
    const div = document.createElement("div");
    // Add classes
    div.className = className;
    // Add text
    div.appendChild(document.createTextNode(message));
    // Get parent
    const container = document.querySelector(".postsContainer");
    // Get posts
    const posts = document.querySelector("#posts");
    // Insert alert div
    container.insertBefore(div, posts);

    // Timeout
    setTimeout(() => {
      this.clearAlert();
    }, 3000);
  }

  // Clear alert message
  clearAlert() {
    const currentAlert = document.querySelector(".alert");

    if (currentAlert) {
      currentAlert.remove();
    }
  }

  // Clear all fields
  clearFields() {
    this.titleInput.value = "";
    this.bodyInput.value = "";
  }

  // Fill form to edit
  fillForm(data) {
    this.titleInput.value = data.title;
    this.bodyInput.value = data.body;
    this.idInput.value = data.id;

    this.changeFormState("edit");
  }

  // Clear Id hidden value
  clearIdInput() {
    this.idInput.vale = "";
  }

  // Change the form state
  changeFormState(type) {
    if (type === "edit") {
      this.postsSubmit.textContent = "Update Post";
      this.postsSubmit.className = "post-submit btn btn-warning";

      //Create a Cancel button
      const button = document.createElement("button");
      button.className = "post-cancel btn btn-light";
      button.appendChild(document.createTextNode("Cancel Edit"));

      //Get Parent
      const div = document.querySelector("#btn-container");
      // get element to insert before
      const formEnd = document.querySelector(".form-end");
      //Insert Cancel Button
      div.insertBefore(button, formEnd);
    } else {
      this.postsSubmit.textContent = "Post It";
      this.postsSubmit.className = "post-submit btn btn-primary";
      // Remove cancel button if it is there
      if (document.querySelector(".post-cancel")) {
        document.querySelector(".post-cancel").remove();
      }
      // Clear id from hidden field
      this.clearIdInput();
      // Clear text
      this.clearFields();
    }
  }
}

export const ui = new UI();
